      serie_comprobante: '',
      num_comprobante: '',
      impuesto: 18,
      codigo: '',
      verNuevo: 0,
      errorArticulo: null,
      totalParcial: 0,
      totalImpuesto: 0,
      total: 0,
      cabeceraArticulos: [{
        text: 'Seleccionar',
        value: 'seleccionar',
        sortable: false
      }, {
        text: 'Artículo',
        value: 'articulo'
      }, {
        text: 'Categoría',
        value: 'categoria'
      }, {
        text: 'Descripción',
        value: 'descripcion',
        sortable: false
      }, {
        text: 'Stock',
        value: 'stock',
        sortable: false
      }, {
        text: 'Precio Venta',
        value: 'precio_venta',
        sortable: false
      }],
      articulos: [],
      texto: '',
      verArticulos: 0,
      verDet: 0,
      valida: 0,
      validaMensaje: [],
      adModal: 0,
      adAccion: 0,
      adNombre: '',
      adId: '',
      comprobanteModal: 0,
      cliente: '',
      fecha_hora: '',
      num_documento: '',
      direccion: '',
      telefono: '',
      email: ''
    };
  },
  computed: {
    calcularTotal: function calcularTotal() {
      var resultado = 0.0;

      for (var i = 0; i < this.detalles.length; i++) {
        resultado = resultado + (this.detalles[i].precio * this.detalles[i].cantidad - this.detalles[i].descuento);
      }

      return resultado;
    }
  },
  watch: {
    dialog: function dialog(val) {
      val || this.close();
    }
  },
  created: function created() {
    this.listar();
    this.select();
  },
  methods: {
    crearPDF: function crearPDF() {
      var quotes = document.getElementById('factura');
      html2canvas(quotes).then(function (canvas) {
        var imgData = canvas.toDataURL('image/png');
        var imgWidth = 210;
        var pageHeight = 295;
        var imgHeight = canvas.height * imgWidth / canvas.width;
        var heightLeft = imgHeight;
        var doc = new jsPDF();
        var position = 0;
        doc.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
        doc.save('ComprobanteVenta.pdf');
      });
    },
    mostrarComprobante: function mostrarComprobante(item) {
      this.limpiar();
      this.tipo_comprobante = item.tipo_comprobante;
      this.serie_comprobante = item.serie_comprobante;
      this.num_comprobante = item.num_comprobante;
      this.cliente = item.cliente;
      this.num_documento = item.num_documento;
      this.direccion = item.direccion;
      this.telefono = item.telefono;
      this.email = item.email;
      this.fecha_hora = item.fecha_hora;
      this.impuesto = item.impuesto;
      this.listarDetalles(item.idventa);
      this.comprobanteModal = 1;
    },
    ocultarComprobante: function ocultarComprobante() {
      this.comprobanteModal = 0;
      this.limpiar();
    },
    mostrarNuevo: function mostrarNuevo() {
      this.verNuevo = 1;
    },
    ocultarNuevo: function ocultarNuevo() {
      this.verNuevo = 0;
      this.limpiar();
    },
    buscarCodigo: function buscarCodigo() {
      var me = this;
      me.errorArticulo = null;
      var header = {
        "Authorization": "Bearer " + this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.get('api/Articulos/BuscarCodigoVenta/' + this.codigo, configuracion).then(function (response) {
        //console.log(response);
        me.agregarDetalle(response.data);
      }).catch(function (error) {
        console.log(error);
        me.errorArticulo = 'No existe el artículo';
      });
    },
    listarArticulo: function listarArticulo() {
      var me = this;
      var header = {
        "Authorization": "Bearer " + this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.get('api/Articulos/ListarVenta/' + me.texto, configuracion).then(function (response) {
        //console.log(response);
        me.articulos = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    mostrarArticulos: function mostrarArticulos() {
      this.verArticulos = 1;
    },
    ocultarArticulos: function ocultarArticulos() {
      this.verArticulos = 0;
    },
    agregarDetalle: function agregarDetalle() {
      var data = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : [];
      this.errorArticulo = null;

      if (this.encuentra(data['idarticulo'])) {
        this.errorArticulo = "El artículo ya ha sido agregado.";
      } else {
        this.detalles.push({
          idarticulo: data['idarticulo'],
          articulo: data['nombre'],
          cantidad: 1,
          precio: data['precio_venta'],
          descuento: 0
        });
      }
    },
    encuentra: function encuentra(id) {
      var sw = 0;

      for (var i = 0; i < this.detalles.length; i++) {
        if (this.detalles[i].idarticulo == id) {
          sw = 1;
        }
      }

      return sw;
    },
    eliminarDetalle: function eliminarDetalle(arr, item) {
      var i = arr.indexOf(item);

      if (i !== -1) {
        arr.splice(i, 1);
      }
    },
    listar: function listar() {
      var me = this;
      var header = {
        "Authorization": "Bearer " + this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      var url = '';

      if (!me.search) {
        url = 'api/Ventas/Listar';
      } else {
        url = 'api/Ventas/ListarFiltro/' + me.search;
      }

      axios.get(url, configuracion).then(function (response) {
        //console.log(response);
        me.ventas = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    listarDetalles: function listarDetalles(id) {
      var me = this;
      var header = {
        "Authorization": "Bearer " + this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.get('api/Ventas/ListarDetalles/' + id, configuracion).then(function (response) {
        //console.log(response);
        me.detalles = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    verDetalles: function verDetalles(item) {
      this.limpiar();
      this.tipo_comprobante = item.tipo_comprobante;
      this.serie_comprobante = item.serie_comprobante;
      this.num_comprobante = item.num_comprobante;
      this.idcliente = item.idcliente;
      this.impuesto = item.impuesto;
      this.listarDetalles(item.idventa);
      this.verNuevo = 1;
      this.verDet = 1;
    },
    select: function select() {
      var me = this;
      var clientesArray = [];
      var header = {
        "Authorization": "Bearer " + this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.get('api/Personas/SelectClientes', configuracion).then(function (response) {
        clientesArray = response.data;
        clientesArray.map(function (x) {
          me.clientes.push({
            text: x.nombre,
            value: x.idpersona
          });
        });
      }).catch(function (error) {
        console.log(error);
      });
    },
    limpiar: function limpiar() {
      this.id = "";
      this.idcliente = "";
      this.tipo_comprobante = "";
      this.serie_comprobante = "";
      this.num_comprobante = "";
      this.impuesto = "18";
      this.codigo = "";
      this.detalles = [];
      this.total = 0;
      this.totalImpuesto = 0;
      this.totalParcial = 0;
      this.verDet = 0;
    },
    guardar: function guardar() {
      if (this.validar()) {
        return;
      }

      var header = {
        "Authorization": "Bearer " + this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      var me = this;
      axios.post('api/Ventas/Crear', {
        'idcliente': me.idcliente,
        'idusuario': me.$store.state.usuario.idusuario,
        'tipo_comprobante': me.tipo_comprobante,
        'serie_comprobante': me.serie_comprobante,
        'num_comprobante': me.num_comprobante,
        'impuesto': me.impuesto,
        'total': me.total,
        'detalles': me.detalles
      }, configuracion).then(function (response) {
        me.ocultarNuevo();
        me.listar();
        me.limpiar();
      }).catch(function (error) {
        console.log(error);
      });
    },
    validar: function validar() {
      this.valida = 0;
      this.validaMensaje = [];

      if (!this.idcliente) {
        this.validaMensaje.push("Seleccione un cliente.");
      }

      if (!this.tipo_comprobante) {
        this.validaMensaje.push("Seleccione un tipo de comprobante.");
      }

      if (!this.num_comprobante) {
        this.validaMensaje.push("Ingrese el número del comprobante.");
      }

      if (!this.impuesto || this.impuesto < 0) {
        this.validaMensaje.push("Ingrese un impuesto válido.");
      }

      if (this.detalles.length <= 0) {
        this.validaMensaje.push("Ingrese al menos un artículo al detalle.");
      }

      if (this.validaMensaje.length) {
        this.valida = 1;
      }

      return this.valida;
    },
    activarDesactivarMostrar: function activarDesactivarMostrar(accion, item) {
      this.adModal = 1;
      this.adNombre = item.num_comprobante;
      this.adId = item.idventa;

      if (accion == 1) {
        this.adAccion = 1;
      } else if (accion == 2) {
        this.adAccion = 2;
      } else {
        this.adModal = 0;
      }
    },
    activarDesactivarCerrar: function activarDesactivarCerrar() {
      this.adModal = 0;
    },

    /*
    activar(){
        let me=this;
        let header={"Authorization" : "Bearer " + this.$store.state.token};
        let configuracion= {headers : header};
        axios.put('api/Usuarios/Activar/'+this.adId,{},configuracion).then(function(response){
            me.adModal=0;
            me.adAccion=0;
            me.adNombre="";
            me.adId="";
            me.listar();                       
        }).catch(function(error){
            console.log(error);
        });
    },
    */
    desactivar: function desactivar() {
      var me = this;
      var header = {
        "Authorization": "Bearer " + this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.put('api/Ventas/Anular/' + this.adId, {}, configuracion).then(function (response) {
        me.adModal = 0;
        me.adAccion = 0;
        me.adNombre = "";
        me.adId = "";
        me.listar();
      }).catch(function (error) {
        console.log(error);
      });
    }
  }
};
