    return {
      email: '',
      password: '',
      error: null
    };
  },
  methods: {
    ingresar: function ingresar() {
      var _this = this;

      this.error = null;
      axios.post('api/Usuarios/Login', {
        email: this.email,
        password: this.password
      }).then(function (respuesta) {
